import React, { useState } from 'react';

// Importiere bei Bedarf Icons, z.B. von Heroicons oder einer anderen Icon-Bibliothek.
// Für dieses Beispiel verwenden wir direkt SVG-Pfade, wie du es begonnen hast.
// import { DocumentTextIcon, MicrophoneIcon, PlayIcon } from '@heroicons/react/24/outline'; // Beispiel, wenn du eine Bibliothek nutzt

interface VideoOptions {
    language: string;
    voice: string;
    resolution: string;
    speechSpeed: string;
    music: string;
    style: string;
}

interface TextInputProps {
    onSubmit: (text: string, options: VideoOptions, isPreview: boolean) => void;
    isLoading?: boolean;
}

const TextInput: React.FC<TextInputProps> = ({ onSubmit, isLoading = false }) => {
    const [text, setText] = useState('');
    const [options, setOptions] = useState<VideoOptions>({
        language: 'de',
        voice: 'anna',
        resolution: '1080p',
        speechSpeed: 'normal',
        music: 'none',
        style: 'explainer'
    });

    const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setText(event.target.value);
    };

    const handleOptionChange = (key: keyof VideoOptions, value: string) => {
        setOptions(prev => ({
            ...prev,
            [key]: value
        }));
    };

    const handlePreviewClick = (event: React.MouseEvent<HTMLButtonElement>) => {
        event.preventDefault(); // Verhindert Formular-Submit
        if (text.trim() && !isLoading) {
            onSubmit(text, options, true);
        }
    };

    const handleVideoClick = (event: React.MouseEvent<HTMLButtonElement>) => {
        event.preventDefault(); // Verhindert Formular-Submit
        if (text.trim() && !isLoading) {
            onSubmit(text, options, false);
        }
    };

    return (
        <div className="space-y-8"> {/* Erhöhter Abstand zwischen den Karten */}
            <form onSubmit={(e) => { e.preventDefault(); /* Dies wird durch die Button-Handler überschrieben */ }} className="space-y-8">
                {/* Text Input Card */}
                <div className="bg-white rounded-xl shadow-md border border-gray-100 p-6 hover:shadow-lg transition-shadow duration-300">
                    <div className="flex items-center mb-4">
                        <div className="w-8 h-8 bg-blue-100 rounded-lg flex items-center justify-center mr-3">
                            {/* Icon für Text Input */}
                            <svg className="w-5 h-5 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z" />
                            </svg>
                        </div>
                        <h2 className="text-xl font-semibold text-gray-800">Text eingeben</h2>
                    </div>
                    <textarea
                        value={text}
                        onChange={handleChange}
                        placeholder="Gib deinen Text hier ein..."
                        rows={6}
                        required
                        disabled={isLoading}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-y transition duration-200 hover:border-gray-400"
                        title="Gib den Text ein, aus dem ein Video generiert werden soll."
                    />
                </div>

                {/* Audio Settings Card */}
                <div className="bg-white rounded-xl shadow-md border border-gray-100 p-6 hover:shadow-lg transition-shadow duration-300">
                    <div className="flex items-center mb-6">
                        <div className="w-8 h-8 bg-green-100 rounded-lg flex items-center justify-center mr-3">
                            {/* Icon für Audio Settings */}
                            <svg className="w-5 h-5 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" />
                            </svg>
                        </div>
                        <h2 className="text-xl font-semibold text-gray-800">Audio-Einstellungen</h2>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Sprache */}
                        <div className="group">
                            <label htmlFor="language-select" className="block text-sm font-medium text-gray-700 mb-2">
                                <div className="flex items-center">
                                    {/* Icon für Sprache */}
                                    <svg className="w-4 h-4 text-gray-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5h12M9 3v2m1.048 9.5A18.022 18.022 0 016.412 9m6.088 9h7M11 21l5-10 5 10M12.751 5C11.783 10.77 8.07 15.61 3 18.129" />
                                    </svg>
                                    Sprache
                                </div>
                            </label>
                            <select
                                id="language-select"
                                value={options.language}
                                onChange={(e) => handleOptionChange('language', e.target.value)}
                                disabled={isLoading}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent hover:border-gray-400 transition-colors duration-200"
                                title="Wähle die Sprache für die Sprachausgabe"
                            >
                                <option value="de">🇩🇪 Deutsch</option>
                                <option value="en">🇺🇸 Englisch</option>
                                <option value="es">🇪🇸 Spanisch</option>
                                <option value="fr">🇫🇷 Französisch</option>
                                <option value="it">🇮🇹 Italienisch</option>
                            </select>
                        </div>

                        {/* Stimme */}
                        <div className="group">
                            <label htmlFor="voice-select" className="block text-sm font-medium text-gray-700 mb-2">
                                <div className="flex items-center">
                                    {/* Icon für Stimme */}
                                    <svg className="w-4 h-4 text-gray-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                                    </svg>
                                    Stimme
                                </div>
                            </label>
                            <select
                                id="voice-select"
                                value={options.voice}
                                onChange={(e) => handleOptionChange('voice', e.target.value)}
                                disabled={isLoading}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent hover:border-gray-400 transition-colors duration-200"
                                title="Wähle die Stimme für die Sprachausgabe"
                            >
                                <option value="anna">👩 Anna (Weiblich)</option>
                                <option value="max">👨 Max (Männlich)</option>
                                <option value="lisa">👩 Lisa (Weiblich)</option>
                                <option value="tom">👨 Tom (Männlich)</option>
                            </select>
                        </div>

                        {/* Sprechtempo */}
                        <div className="group md:col-span-2"> {/* Nimmt 2 Spalten auf großen Bildschirmen ein */}
                            <label htmlFor="speech-speed-select" className="block text-sm font-medium text-gray-700 mb-2">
                                <div className="flex items-center">
                                    {/* Icon für Sprechtempo */}
                                    <svg className="w-4 h-4 text-gray-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                                    </svg>
                                    Sprechtempo
                                </div>
                            </label>
                            <select
                                id="speech-speed-select"
                                value={options.speechSpeed}
                                onChange={(e) => handleOptionChange('speechSpeed', e.target.value)}
                                disabled={isLoading}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent hover:border-gray-400 transition-colors duration-200"
                                title="Wähle das Sprechtempo"
                            >
                                <option value="slow">🐌 Langsam</option>
                                <option value="normal">⚡ Normal</option>
                                <option value="fast">🚀 Schnell</option>
                            </select>
                        </div>
                    </div>
                </div>

                {/* Video Settings Card */}
                <div className="bg-white rounded-xl shadow-md border border-gray-100 p-6 hover:shadow-lg transition-shadow duration-300">
                    <div className="flex items-center mb-6">
                        <div className="w-8 h-8 bg-purple-100 rounded-lg flex items-center justify-center mr-3">
                            {/* Icon für Video Settings */}
                            <svg className="w-5 h-5 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 10l4.553-2.276A1 1 0 0121 8.618v6.764a1 1 0 01-1.447.894L15 14M5 18h8a2 2 0 002-2V8a2 2 0 00-2-2H5a2 2 0 00-2 2v8a2 2 0 002 2z" />
                            </svg>
                        </div>
                        <h2 className="text-xl font-semibold text-gray-800">Video-Einstellungen</h2>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        {/* Auflösung */}
                        <div className="group">
                            <label htmlFor="resolution-select" className="block text-sm font-medium text-gray-700 mb-2">
                                <div className="flex items-center">
                                    {/* Icon für Auflösung */}
                                    <svg className="w-4 h-4 text-gray-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.75 17L9 20l-1 1h8l-1-1-.75-3M3 13h18M5 17h14a2 2 0 002-2V5a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                                    </svg>
                                    Auflösung
                                </div>
                            </label>
                            <select
                                id="resolution-select"
                                value={options.resolution}
                                onChange={(e) => handleOptionChange('resolution', e.target.value)}
                                disabled={isLoading}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent hover:border-gray-400 transition-colors duration-200"
                                title="Wähle die Video-Auflösung"
                            >
                                <option value="720p">📺 720p (HD)</option>
                                <option value="1080p">🎬 1080p (Full HD)</option>
                                <option value="1440p">🖥️ 1440p (2K)</option>
                                <option value="2160p">📽️ 2160p (4K)</option>
                            </select>
                        </div>

                        {/* Video-Stil */}
                        <div className="group">
                            <label htmlFor="style-select" className="block text-sm font-medium text-gray-700 mb-2">
                                <div className="flex items-center">
                                    {/* Icon für Video-Stil */}
                                    <svg className="w-4 h-4 text-gray-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 4V2a1 1 0 011-1h8a1 1 0 011 1v2m0 0V1a1 1 0 011-1h2a1 1 0 011 1v18a1 1 0 01-1 1H4a1 1 0 01-1-1V1a1 1 0 011-1h2a1 1 0 011 1v3" />
                                    </svg>
                                    Video-Stil
                                </div>
                            </label>
                            <select
                                id="style-select"
                                value={options.style}
                                onChange={(e) => handleOptionChange('style', e.target.value)}
                                disabled={isLoading}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent hover:border-gray-400 transition-colors duration-200"
                                title="Wähle den Video-Stil"
                            >
                                <option value="explainer">💡 Erklärvideo</option>
                                <option value="presentation">📊 Präsentation</option>
                                <option value="tutorial">🎓 Tutorial</option>
                                <option value="news">📰 Nachrichten</option>
                                <option value="storytelling">📚 Storytelling</option>
                                <option value="minimal">⚪ Minimalistisch</option>
                            </select>
                        </div>

                        {/* Hintergrundmusik */}
                        <div className="group md:col-span-2">
                            <label htmlFor="music-select" className="block text-sm font-medium text-gray-700 mb-2">
                                <div className="flex items-center">
                                    {/* Icon für Hintergrundmusik (vervollständigt) */}
                                    <svg className="w-4 h-4 text-gray-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19V6l12-3v13M3 19h18" />
                                    </svg>
                                    Hintergrundmusik
                                </div>
                            </label>
                            <select
                                id="music-select"
                                value={options.music}
                                onChange={(e) => handleOptionChange('music', e.target.value)}
                                disabled={isLoading}
                                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent hover:border-gray-400 transition-colors duration-200"
                                title="Wähle die Hintergrundmusik für das Video"
                            >
                                <option value="none">🔇 Keine</option>
                                <option value="relaxed">🧘 Entspannt</option>
                                <option value="energetic">⚡ Energisch</option>
                                <option value="corporate">🏢 Business</option>
                                <option value="upbeat"> upbeat</option>
                                <option value="ambient">🌃 Ambient</option>
                            </select>
                        </div>
                    </div>
                </div>

                {/* Zwei-Button-System */}
                <div className="flex flex-col sm:flex-row gap-4 justify-center">
                    <button 
                        type="button"
                        onClick={handlePreviewClick}
                        disabled={isLoading || !text.trim()}
                        className={`px-6 py-3 rounded-lg font-medium transition duration-200 ${
                            isLoading || !text.trim() 
                                ? 'bg-gray-400 text-white cursor-not-allowed' 
                                : 'bg-gray-600 hover:bg-gray-700 text-white'
                        }`}
                        title="Generiere eine Vorschau des Videos (ohne Speicherung)."
                    >
                        {isLoading ? 'Wird verarbeitet...' : 'Vorschau generieren'}
                    </button>

                    <button 
                        type="button"
                        onClick={handleVideoClick}
                        disabled={isLoading || !text.trim()}
                        className={`px-6 py-3 rounded-lg font-medium text-white transition duration-200 ${
                            isLoading || !text.trim() 
                                ? 'bg-gray-400 cursor-not-allowed' 
                                : 'bg-blue-600 hover:bg-blue-700'
                        }`}
                        title="Generiere das vollständige Video und speichere es."
                    >
                        {isLoading ? 'Wird verarbeitet...' : 'Video generieren'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default TextInput;
