import React, { useState } from 'react';
import TextInput from './components/TextInput';
import VideoDisplay from './components/VideoDisplay';

interface VideoOptions {
    language: string;
    voice: string;
    resolution: string;
    speechSpeed: string;
    music: string;
    style: string;
}

const App: React.FC = () => {
    interface VideoData {
        url: string;
        isPreview?: boolean;
        // Add other video data properties as needed
    }

    const [videoData, setVideoData] = useState<VideoData | null>(null);
    const [previewData, setPreviewData] = useState<VideoData | null>(null);
    const [isLoading, setIsLoading] = useState<boolean>(false);
    const [error, setError] = useState<string | null>(null);
    const [loadingType, setLoadingType] = useState<'preview' | 'video' | null>(null);

    const handleTextSubmit = async (text: string, options: VideoOptions, isPreview: boolean) => {
        try {
            setIsLoading(true);
            setError(null);
            setLoadingType(isPreview ? 'preview' : 'video');

            const endpoint = isPreview ? '/api/generate-preview' : '/api/generate-video';

            const response = await fetch(`http://localhost:5000${endpoint}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ 
                    text,
                    options 
                }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Ein Fehler ist aufgetreten');
            }

            const data = await response.json();
            const videoResult = { ...data, isPreview };

            if (isPreview) {
                setPreviewData(videoResult);
            } else {
                setVideoData(videoResult);
                setPreviewData(null); // Clear preview when generating full video
            }
        } catch (error) {
            console.error('Error generating video:', error);
            setError(error instanceof Error ? error.message : 'Ein unbekannter Fehler ist aufgetreten');
        } finally {
            setIsLoading(false);
            setLoadingType(null);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8 px-4">
            <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-lg overflow-hidden">
                <div className="p-8">
                    <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">Text zu Video Konverter</h1>

                    <TextInput onSubmit={handleTextSubmit} isLoading={isLoading} />

                    {error && (
                        <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
                            <p className="text-red-600">{error}</p>
                        </div>
                    )}

                    {isLoading && (
                        <div className="flex flex-col items-center justify-center mt-8">
                            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
                            <p className="mt-4 text-gray-600">
                                {loadingType === 'preview' 
                                    ? 'Vorschau wird generiert, bitte warten...' 
                                    : 'Video wird generiert, bitte warten...'
                                }
                            </p>
                        </div>
                    )}

                    {/* Vorschau-Video anzeigen */}
                    {!isLoading && previewData && (
                        <div className="mt-6">
                            <h3 className="text-lg font-semibold text-gray-700 mb-4 text-center">Vorschau</h3>
                            <VideoDisplay videoData={previewData} />
                        </div>
                    )}

                    {/* Finales Video anzeigen */}
                    {!isLoading && videoData && !videoData.isPreview && (
                        <div className="mt-6">
                            <h3 className="text-lg font-semibold text-gray-700 mb-4 text-center">Finales Video</h3>
                            <VideoDisplay videoData={videoData} />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default App;
