import React, { useState } from 'react';
import TextInput from './components/TextInput';
import VideoDisplay from './components/VideoDisplay';

const App: React.FC = () => {
    interface VideoData {
        url: string;
        type?: string;
        message?: string;
    }

    const [videoData, setVideoData] = useState<VideoData | null>(null);
    const [isLoading, setIsLoading] = useState<boolean>(false);
    const [error, setError] = useState<string | null>(null);

    const handleTextSubmit = async (text: string, animationType: string) => {
        try {
            setIsLoading(true);
            setError(null);

            // WÃ¤hle API-Endpoint basierend auf Animation-Typ
            const endpoint = animationType === 'static' 
                ? 'http://localhost:5000/api/generate-video'
                : 'http://localhost:5000/api/generate-animated-video';

            const response = await fetch(endpoint, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ 
                    text,
                    animation_type: animationType 
                }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Ein Fehler ist aufgetreten');
            }

            const data = await response.json();
            setVideoData(data);
        } catch (error) {
            console.error('Error generating video:', error);
            setError(error instanceof Error ? error.message : 'Ein unbekannter Fehler ist aufgetreten');
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-100 py-8 px-4">
            <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-lg overflow-hidden">
                <div className="p-8">
                    <h1 className="text-3xl font-bold text-center text-gray-800 mb-2">
                        ðŸŽ¬ Animierter Video Generator
                    </h1>
                    <p className="text-center text-gray-600 mb-8">
                        Erstelle echte Animationen mit AI! Katzen wedeln mit SchwÃ¤nzen, BÃ¤ume bewegen sich im Wind...
                    </p>

                    <TextInput onSubmit={handleTextSubmit} isLoading={isLoading} />

                    {error && (
                        <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
                            <p className="text-red-600">{error}</p>
                        </div>
                    )}

                    {isLoading && (
                        <div className="flex flex-col items-center justify-center mt-8">
                            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-purple-500"></div>
                            <p className="mt-4 text-gray-600">
                                ðŸŽ¬ Erstelle echte Animation... Das kann 5-10 Minuten dauern!
                            </p>
                                                        <p className="text-sm text-gray-500 mt-2">
                                AI generiert bewegte Bilder mit echten Animationen...
                            </p>
                        </div>
                    )}

                    {!isLoading && videoData && (
                        <div className="mt-6">
                            {videoData.message && (
                                <div className="mb-4 p-3 bg-green-50 border border-green-200 rounded-lg">
                                    <p className="text-green-700">âœ… {videoData.message}</p>
                                    <p className="text-sm text-green-600 mt-1">
                                        {videoData.type === 'animated' ? 'ðŸŽ¬ Echte Animation erstellt!' : 'ðŸ“¸ Statisches Video erstellt'}
                                    </p>
                                </div>
                            )}
                            <VideoDisplay videoData={videoData} />
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default App;
