import React, { useState } from 'react';

interface VideoOptions {
    language: string;
    voice: string;
    resolution: string;
    speechSpeed: string;
}

interface TextInputProps {
    onSubmit: (text: string, options: VideoOptions) => void;
    isLoading?: boolean;
}

const TextInput: React.FC<TextInputProps> = ({ onSubmit, isLoading = false }) => {
    const [text, setText] = useState('');
    const [options, setOptions] = useState<VideoOptions>({
        language: 'de',
        voice: 'anna',
        resolution: '1080p',
        speechSpeed: 'normal'
    });

    const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setText(event.target.value);
    };

    const handleOptionChange = (key: keyof VideoOptions, value: string) => {
        setOptions(prev => ({
            ...prev,
            [key]: value
        }));
    };

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (text.trim() && !isLoading) {
            onSubmit(text, options);
        }
    };

    return (
        <div className="space-y-6">
            <form onSubmit={handleSubmit} className="space-y-6">
                {/* Text Input */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                        Text eingeben
                    </label>
                    <textarea
                        value={text}
                        onChange={handleChange}
                        placeholder="Gib deinen Text hier ein..."
                        rows={6}
                        required
                        disabled={isLoading}
                        className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-y transition duration-200"
                    />
                </div>

                {/* Options Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* Sprache */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Sprache
                        </label>
                        <select
                            value={options.language}
                            onChange={(e) => handleOptionChange('language', e.target.value)}
                            disabled={isLoading}
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        >
                            <option value="de">Deutsch</option>
                            <option value="en">Englisch</option>
                            <option value="es">Spanisch</option>
                            <option value="fr">Französisch</option>
                            <option value="it">Italienisch</option>
                        </select>
                    </div>

                    {/* Stimme */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Stimme
                        </label>
                        <select
                            value={options.voice}
                            onChange={(e) => handleOptionChange('voice', e.target.value)}
                            disabled={isLoading}
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        >
                            <option value="anna">Anna (Weiblich)</option>
                            <option value="max">Max (Männlich)</option>
                            <option value="lisa">Lisa (Weiblich)</option>
                            <option value="tom">Tom (Männlich)</option>
                        </select>
                    </div>

                    {/* Auflösung */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Auflösung
                        </label>
                        <select
                            value={options.resolution}
                            onChange={(e) => handleOptionChange('resolution', e.target.value)}
                            disabled={isLoading}
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        >
                            <option value="720p">720p (HD)</option>
                            <option value="1080p">1080p (Full HD)</option>
                            <option value="1440p">1440p (2K)</option>
                            <option value="2160p">2160p (4K)</option>
                        </select>
                    </div>

                    {/* Sprechtempo */}
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Sprechtempo: {options.speechSpeed === 'slow' ? 'Langsam' : options.speechSpeed === 'normal' ? 'Normal' : 'Schnell'}
                        </label>
                        <select
                            value={options.speechSpeed}
                            onChange={(e) => handleOptionChange('speechSpeed', e.target.value)}
                            disabled={isLoading}
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        >
                            <option value="slow">Langsam</option>
                            <option value="normal">Normal</option>
                            <option value="fast">Schnell</option>
                        </select>
                    </div>
                </div>

                {/* Submit Button */}
                <div className="flex justify-center">
                    <button 
                        type="submit" 
                        disabled={isLoading || !text.trim()}
                        className={`px-6 py-3 rounded-lg font-medium text-white transition duration-200 ${
                            isLoading || !text.trim() 
                                ? 'bg-gray-400 cursor-not-allowed' 
                                : 'bg-blue-600 hover:bg-blue-700'
                        }`}
                    >
                        {isLoading ? 'Wird verarbeitet...' : 'Video generieren'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default TextInput;
