import React, { useState } from 'react';

interface TextInputProps {
    onSubmit: (text: string, animationType: string) => void;
    isLoading?: boolean;
}

const TextInput: React.FC<TextInputProps> = ({ onSubmit, isLoading = false }) => {
    const [text, setText] = useState('');
    const [animationType, setAnimationType] = useState('combined');

    const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setText(event.target.value);
    };

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (text.trim() && !isLoading) {
            onSubmit(text, animationType);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <textarea
                    value={text}
                    onChange={handleChange}
                    placeholder="Beschreibe deine Animation: z.B. 'Eine Katze sitzt auf dem Mond und wedelt mit dem Schwanz'"
                    rows={6}
                    required
                    disabled={isLoading}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-y transition duration-200"
                />

                {/* Animation Type Selector */}
                <div className="mt-4">
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                        Animation-Typ:
                    </label>
                    <select
                        value={animationType}
                        onChange={(e) => setAnimationType(e.target.value)}
                        disabled={isLoading}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                        <option value="combined">üé¨ Kombiniert (Text + Bild Animation)</option>
                        <option value="text">üìù Text zu Animation</option>
                        <option value="image">üñºÔ∏è Bild zu Animation</option>
                        <option value="static">üì∏ Statisch (alte Version)</option>
                    </select>
                </div>

                <div className="mt-4 flex justify-center">
                    <button 
                        type="submit" 
                        disabled={isLoading || !text.trim()}
                        className={`px-6 py-3 rounded-lg font-medium text-white transition duration-200 ${
                            isLoading || !text.trim() 
                                ? 'bg-gray-400 cursor-not-allowed' 
                                : 'bg-blue-600 hover:bg-blue-700'
                        }`}
                    >
                        {isLoading ? 'Erstelle Animation...' : 'üé¨ Animiertes Video generieren'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default TextInput;
