import React, { useState } from 'react';

interface TextInputProps {
    onSubmit: (text: string) => void;
    isLoading?: boolean;
}

const TextInput: React.FC<TextInputProps> = ({ onSubmit, isLoading = false }) => {
    const [text, setText] = useState('');

    const handleChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
        setText(event.target.value);
    };

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (text.trim() && !isLoading) {
            onSubmit(text);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <textarea
                    value={text}
                    onChange={handleChange}
                    placeholder="Gib deinen Text hier ein..."
                    rows={6}
                    required
                    disabled={isLoading}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-y transition duration-200"
                />
                <div className="mt-4 flex justify-center">
                    <button 
                        type="submit" 
                        disabled={isLoading || !text.trim()}
                        className={`px-6 py-3 rounded-lg font-medium text-white transition duration-200 ${
                            isLoading || !text.trim() 
                                ? 'bg-gray-400 cursor-not-allowed' 
                                : 'bg-blue-600 hover:bg-blue-700'
                        }`}
                    >
                        {isLoading ? 'Wird verarbeitet...' : 'Video generieren'}
                    </button>
                </div>
            </form>
        </div>
    );
};

export default TextInput;
