import React, { useState, useEffect } from 'react';
import TextInput from './components/TextInput';
import VideoDisplay from './components/VideoDisplay';

const App: React.FC = () => {
    interface VideoData {
        url: string;
    }

    interface TTSSettings {
        language: string;
        voice: string;
        speech_speed: string;
        auto_translate: boolean;
        enhance_quality: boolean;
        is_preview: boolean;
    }

    interface SupportedLanguage {
        code: string;
        name: string;
        quality: string;
    }

    interface SupportedVoice {
        code: string;
        gender: string;
        accent: string;
        description: string;
    }

    interface SupportedSpeed {
        code: string;
        modifier: number;
        slow_mode: boolean;
    }

    interface ValidationResult {
        valid: boolean;
        errors: string[];
        warnings: string[];
        estimated_duration: number;
    }

    interface OptimalSettings {
        recommended_settings: TTSSettings;
        text_analysis: {
            word_count: number;
            character_count: number;
            detected_language: string;
            has_numbers: boolean;
            has_abbreviations: boolean;
            estimated_duration: number;
        };
        reasoning: string;
    }

    // State Management
    const [videoData, setVideoData] = useState<VideoData | null>(null);
    const [isLoading, setIsLoading] = useState<boolean>(false);
    const [error, setError] = useState<string | null>(null);
    const [showAdvancedSettings, setShowAdvancedSettings] = useState<boolean>(false);

    // TTS Settings State
    const [ttsSettings, setTtsSettings] = useState<TTSSettings>({
        language: 'de',
        voice: 'anna',
        speech_speed: 'normal',
        auto_translate: true,
        enhance_quality: true,
        is_preview: false
    });

    // Available Options State
    const [supportedLanguages, setSupportedLanguages] = useState<SupportedLanguage[]>([]);
    const [supportedVoices, setSupportedVoices] = useState<SupportedVoice[]>([]);
    const [supportedSpeeds, setSupportedSpeeds] = useState<SupportedSpeed[]>([]);

    // Additional State
    const [validationResult, setValidationResult] = useState<ValidationResult | null>(null);
    const [optimalSettings, setOptimalSettings] = useState<OptimalSettings | null>(null);
    const [currentText, setCurrentText] = useState<string>('');
    const [loadingMessage, setLoadingMessage] = useState<string>('');

    // Load supported options on component mount
    useEffect(() => {
        loadSupportedOptions();
    }, []);

    // Validate input when text or settings change
    useEffect(() => {
        if (currentText.trim()) {
            validateInput(currentText);
            getOptimalSettings(currentText);
        }
    }, [currentText, ttsSettings]);

    const loadSupportedOptions = async () => {
        try {
            // Load supported languages
            const langResponse = await fetch('http://localhost:5000/api/tts/languages');
            if (langResponse.ok) {
                const languages = await langResponse.json();
                setSupportedLanguages(languages);
            }

            // Load supported voices
            const voiceResponse = await fetch('http://localhost:5000/api/tts/voices');
            if (voiceResponse.ok) {
                const voices = await voiceResponse.json();
                setSupportedVoices(voices);
            }

            // Load supported speeds
            const speedResponse = await fetch('http://localhost:5000/api/tts/speeds');
            if (speedResponse.ok) {
                const speeds = await speedResponse.json();
                setSupportedSpeeds(speeds);
            }
        } catch (error) {
            console.warn('Could not load TTS options, using defaults');
        }
    };

    const validateInput = async (text: string) => {
        try {
            const response = await fetch('http://localhost:5000/api/tts/validate', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    text,
                    language: ttsSettings.language,
                    voice: ttsSettings.voice,
                    speech_speed: ttsSettings.speech_speed
                }),
            });

            if (response.ok) {
                const result = await response.json();
                setValidationResult(result);
            }
        } catch (error) {
            console.warn('Validation failed:', error);
        }
    };

    const getOptimalSettings = async (text: string) => {
        try {
            const response = await fetch('http://localhost:5000/api/tts/optimal-settings', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    text,
                    use_case: 'general'
                }),
            });

            if (response.ok) {
                const result = await response.json();
                setOptimalSettings(result);
            }
        } catch (error) {
            console.warn('Could not get optimal settings:', error);
        }
    };

    const applyOptimalSettings = () => {
        if (optimalSettings?.recommended_settings) {
            setTtsSettings(prev => ({
                ...prev,
                ...optimalSettings.recommended_settings
            }));
        }
    };

    const handleTextSubmit = async (text: string) => {
        try {
            setIsLoading(true);
            setError(null);
            setCurrentText(text);
            setLoadingMessage('Text wird analysiert...');

            // First validate the input
            await validateInput(text);

            if (validationResult && !validationResult.valid) {
                throw new Error(validationResult.errors.join(', '));
            }

            setLoadingMessage('Audio wird generiert...');

            const response = await fetch('http://localhost:5000/api/generate-video-advanced', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ 
                    text,
                    ...ttsSettings
                }),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.error || 'Ein Fehler ist aufgetreten');
            }

            setLoadingMessage('Video wird erstellt...');
            const data = await response.json();
            setVideoData(data);

        } catch (error) {
            console.error('Error generating video:', error);
            setError(error instanceof Error ? error.message : 'Ein unbekannter Fehler ist aufgetreten');
        } finally {
            setIsLoading(false);
            setLoadingMessage('');
        }
    };

    const handlePreview = async () => {
        if (!currentText.trim()) return;

        try {
            setIsLoading(true);
            setLoadingMessage('Vorschau wird generiert...');

            const response = await fetch('http://localhost:5000/api/tts/preview', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    text: currentText,
                    ...ttsSettings,
                    is_preview: true
                }),
            });

            if (response.ok) {
                const result = await response.json();
                // Play preview audio
                const audio = new Audio(`http://localhost:5000${result.audio_file}`);
                audio.play();
            }
        } catch (error) {
            console.error('Preview failed:', error);
        } finally {
            setIsLoading(false);
            setLoadingMessage('');
        }
    };

    const updateTTSSetting = (key: keyof TTSSettings, value: any) => {
        setTtsSettings(prev => ({
            ...prev,
            [key]: value
        }));
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8 px-4">
            <div className="max-w-6xl mx-auto bg-white rounded-xl shadow-lg overflow-hidden">
                <div className="p-8">
                    <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
                        Erweiterte Text zu Video Konvertierung
                    </h1>

                    {/* Main Text Input */}
                    <div className="mb-6">
                        <TextInput 
                            onSubmit={handleTextSubmit} 
                            isLoading={isLoading}
                            onTextChange={setCurrentText}
                        />
                    </div>

                    {/* Validation Results */}
                    {validationResult && (
                        <div className="mb-6">
                            {validationResult.errors.length > 0 && (
                                <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
                                    <h4 className="font-semibold text-red-800 mb-2">Fehler:</h4>
                                    <ul className="list-disc list-inside text-red-600">
                                        {validationResult.errors.map((error, index) => (
                                            <li key={index}>{error}</li>
                                        ))}
                                    </ul>
                                </div>
                            )}

                            {validationResult.warnings.length > 0 && (
                                <div className="mb-4 p-4 bg-yellow-50 border border-yellow-200 rounded-lg">
                                    <h4 className="font-semibold text-yellow-800 mb-2">Warnungen:</h4>
                                    <ul className="list-disc list-inside text-yellow-600">
                                        {validationResult.warnings.map((warning, index) => (
                                            <li key={index}>{warning}</li>
                                        ))}
                                    </ul>
                                </div>
                            )}

                            {validationResult.valid && (
                                <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-lg">
                                    <p className="text-green-600">
                                        ✅ Eingabe ist valid. Geschätzte Dauer: {validationResult.estimated_duration}s
                                    </p>
                                </div>
                            )}
                        </div>
                    )}

                    {/* Optimal Settings Suggestion */}
                    {optimalSettings && (
                        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                            <div className="flex justify-between items-start">
                                <div>
                                    <h4 className="font-semibold text-blue-800 mb-2">💡 Empfohlene Einstellungen</h4>
                                    <p className="text-blue-600 text-sm mb-2">{optimalSettings.reasoning}</p>
                                    <div className="text-sm text-blue-600">
                                        <span>Sprache: {optimalSettings.recommended_settings.language}</span>
                                        <span className="mx-2">•</span>
                                        <span>Stimme: {optimalSettings.recommended_settings.voice}</span>
                                        <span className="mx-2">•</span>
                                        <span>Geschwindigkeit: {optimalSettings.recommended_settings.speech_speed}</span>
                                    </div>
                                </div>
                                <button
                                    onClick={applyOptimalSettings}
                                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition duration-200"
                                >
                                    Anwenden
                                </button>
                            </div>
                        </div>
                    )}

                    {/* Advanced Settings Toggle */}
                    <div className="mb-6">
                        <button
                            onClick={() => setShowAdvancedSettings(!showAdvancedSettings)}
                            className="flex items-center text-gray-600 hover:text-gray-800 transition duration-200"
                        >
                            <span className="mr-2">⚙️</span>
                            Erweiterte Einstellungen
                            <span className="ml-2">{showAdvancedSettings ? '▼' : '▶'}</span>
                        </button>
                    </div>

                    {/* Advanced Settings Panel */}
                    {showAdvancedSettings && (
                        <div className="mb-6 p-6 bg-gray-50 rounded-lg border">
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">

                                {/* Language Selection */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Sprache
                                    </label>
                                    <select
                                        value={ttsSettings.language}
                                        onChange={(e) => updateTTSSetting('language', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                    >
                                        {supportedLanguages.map(lang => (
                                            <option key={lang.code} value={lang.code}>
                                                {lang.name} ({lang.quality})
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                {/* Voice Selection */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Stimme
                                    </label>
                                    <select
                                        value={ttsSettings.voice}
                                        onChange={(e) => updateTTSSetting('voice', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                    >
                                        {supportedVoices.map(voice => (
                                            <option key={voice.code} value={voice.code}>
                                                {voice.description} ({voice.gender}, {voice.accent})
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                {/* Speed Selection */}
                                <div>
                                    <label className="block text-sm font-medium text-gray-700 mb-2">
                                        Geschwindigkeit
                                    </label>
                                    <select
                                        value={ttsSettings.speech_speed}
                                        onChange={(e) => updateTTSSetting('speech_speed', e.target.value)}
                                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                    >
                                        {supportedSpeeds.map(speed => (
                                            <option key={speed.code} value={speed.code}>
                                                {speed.code} {speed.slow_mode ? '(Langsam)' : ''}
                                            </option>
                                        ))}
                                    </select>
                                </div>
                            </div>

                            {/* Additional Settings Row */}
                            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">

                                {/* Auto Translate Toggle */}
                                <div className="flex items-center">
                                    <input
                                        type="checkbox"
                                        id="auto-translate"
                                        checked={ttsSettings.auto_translate}
                                        onChange={(e) => updateTTSSetting('auto_translate', e.target.checked)}
                                        className="mr-2 h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                    />
                                    <label htmlFor="auto-translate" className="text-sm font-medium text-gray-700">
                                        Automatische Übersetzung
                                    </label>
                                </div>

                                {/* Enhance Quality Toggle */}
                                <div className="flex items-center">
                                    <input
                                        type="checkbox"
                                        id="enhance-quality"
                                        checked={ttsSettings.enhance_quality}
                                        onChange={(e) => updateTTSSetting('enhance_quality', e.target.checked)}
                                        className="mr-2 h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                    />
                                    <label htmlFor="enhance-quality" className="text-sm font-medium text-gray-700">
                                        Qualität verbessern
                                    </label>
                                </div>

                                {/* Preview Mode Toggle */}
                                <div className="flex items-center">
                                    <input
                                        type="checkbox"
                                        id="preview-mode"
                                        checked={ttsSettings.is_preview}
                                        onChange={(e) => updateTTSSetting('is_preview', e.target.checked)}
                                        className="mr-2 h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                                    />
                                    <label htmlFor="preview-mode" className="text-sm font-medium text-gray-700">
                                        Vorschau-Modus
                                    </label>
                                </div>
                            </div>

                            {/* Action Buttons */}
                            <div className="flex justify-center space-x-4 mt-6">
                                <button
                                    onClick={handlePreview}
                                    disabled={!currentText.trim() || isLoading}
                                    className={`px-4 py-2 rounded-lg font-medium transition duration-200 ${
                                        !currentText.trim() || isLoading
                                            ? 'bg-gray-400 text-gray-600 cursor-not-allowed'
                                            : 'bg-green-600 text-white hover:bg-green-700'
                                    }`}
                                >
                                    🔊 Audio-Vorschau
                                </button>

                                <button
                                    onClick={() => setTtsSettings({
                                        language: 'de',
                                        voice: 'anna',
                                        speech_speed: 'normal',
                                        auto_translate: true,
                                        enhance_quality: true,
                                        is_preview: false
                                    })}
                                    className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200"
                                >
                                    🔄 Zurücksetzen
                                </button>
                            </div>
                        </div>
                    )}

                    {/* Text Analysis Display */}
                    {optimalSettings?.text_analysis && (
                        <div className="mb-6 p-4 bg-gray-50 rounded-lg border">
                            <h4 className="font-semibold text-gray-800 mb-3">📊 Text-Analyse</h4>
                            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                <div>
                                    <span className="text-gray-600">Wörter:</span>
                                    <span className="ml-2 font-medium">{optimalSettings.text_analysis.word_count}</span>
                                </div>
                                <div>
                                    <span className="text-gray-600">Zeichen:</span>
                                    <span className="ml-2 font-medium">{optimalSettings.text_analysis.character_count}</span>
                                </div>
                                <div>
                                    <span className="text-gray-600">Sprache:</span>
                                    <span className="ml-2 font-medium">{optimalSettings.text_analysis.detected_language}</span>
                                </div>
                                <div>
                                    <span className="text-gray-600">Dauer:</span>
                                    <span className="ml-2 font-medium">{optimalSettings.text_analysis.estimated_duration}s</span>
                                </div>
                            </div>

                            {/* Additional Analysis Info */}
                            <div className="mt-3 flex flex-wrap gap-2">
                                {optimalSettings.text_analysis.has_numbers && (
                                    <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                                        Enthält Zahlen
                                    </span>
                                )}
                                {optimalSettings.text_analysis.has_abbreviations && (
                                    <span className="px-2 py-1 bg-purple-100 text-purple-800 text-xs rounded-full">
                                        Enthält Abkürzungen
                                    </span>
                                )}
                            </div>
                        </div>
                    )}

                    {/* Error Display */}
                    {error && (
                        <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
                            <div className="flex items-start">
                                <span className="text-red-500 mr-2">❌</span>
                                <div>
                                    <h4 className="font-semibold text-red-800 mb-1">Fehler aufgetreten</h4>
                                    <p className="text-red-600">{error}</p>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Loading Display */}
                    {isLoading && (
                        <div className="flex flex-col items-center justify-center mb-6 py-8">
                            <div className="relative">
                                <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-blue-500"></div>
                                <div className="absolute inset-0 flex items-center justify-center">
                                    <div className="animate-pulse h-8 w-8 bg-blue-500 rounded-full"></div>
                                </div>
                            </div>
                            <div className="mt-4 text-center">
                                <p className="text-lg font-medium text-gray-700">{loadingMessage || 'Wird verarbeitet...'}</p>
                                <p className="text-sm text-gray-500 mt-1">Dies kann einen Moment dauern</p>
                            </div>

                            {/* Progress Steps */}
                            <div className="mt-6 flex items-center space-x-4">
                                <div className={`flex items-center ${loadingMessage.includes('analysiert') ? 'text-blue-600' : 'text-gray-400'}`}>
                                    <div className={`w-3 h-3 rounded-full mr-2 ${loadingMessage.includes('analysiert') ? 'bg-blue-600 animate-pulse' : 'bg-gray-300'}`}></div>
                                    <span className="text-sm">Analyse</span>
                                </div>
                                <div className={`flex items-center ${loadingMessage.includes('Audio') ? 'text-blue-600' : 'text-gray-400'}`}>
                                    <div className={`w-3 h-3 rounded-full mr-2 ${loadingMessage.includes('Audio') ? 'bg-blue-600 animate-pulse' : 'bg-gray-300'}`}></div>
                                    <span className="text-sm">Audio</span>
                                </div>
                                <div className={`flex items-center ${loadingMessage.includes('Video') ? 'text-blue-600' : 'text-gray-400'}`}>
                                    <div className={`w-3 h-3 rounded-full mr-2 ${loadingMessage.includes('Video') ? 'bg-blue-600 animate-pulse' : 'bg-gray-300'}`}></div>
                                    <span className="text-sm">Video</span>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Video Display */}
                    {!isLoading && videoData && (
                        <div className="mb-6">
                            <VideoDisplay videoData={videoData} />

                            {/* Additional Video Actions */}
                            <div className="mt-4 flex justify-center space-x-4">
                                <button
                                    onClick={() => {
                                        const link = document.createElement('a');
                                        link.href = videoData.url.startsWith('http') ? videoData.url : `http://localhost:5000${videoData.url}`;
                                        link.download = 'generated-video.mp4';
                                        link.click();
                                    }}
                                    className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition duration-200"
                                >
                                    📥 Video herunterladen
                                </button>

                                <button
                                    onClick={() => {
                                        setVideoData(null);
                                        setCurrentText('');
                                        setValidationResult(null);
                                        setOptimalSettings(null);
                                        setError(null);
                                    }}
                                    className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition duration-200"
                                >
                                    🔄 Neues Video erstellen
                                </button>
                            </div>
                        </div>
                    )}

                    {/* Statistics and Info Footer */}
                    <div className="mt-8 pt-6 border-t border-gray-200">
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center text-sm text-gray-600">
                            <div>
                                <span className="font-medium">Unterstützte Sprachen:</span>
                                <span className="ml-1">{supportedLanguages.length}</span>
                            </div>
                            <div>
                                <span className="font-medium">Verfügbare Stimmen:</span>
                                <span className="ml-1">{supportedVoices.length}</span>
                            </div>
                            <div>
                                <span className="font-medium">Geschwindigkeiten:</span>
                                <span className="ml-1">{supportedSpeeds.length}</span>
                            </div>
                        </div>

                        {/* Help Text */}
                        <div className="mt-4 text-center text-xs text-gray-500">
                            <p>💡 Tipp: Verwenden Sie die erweiterten Einstellungen für bessere Ergebnisse</p>
                            <p>🎯 Die automatischen Empfehlungen helfen bei der optimalen Konfiguration</p>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default App;
